function Article(){var a={};a.checklist=function(){var b=this;$("#article-list ul").children("li").bind("click",function(a){var c=$(a.target).data("id");b.fetchArticle(c)})};a.fetchArticle=function(b){$.ajax({type:"GET",url:"/article/fetch-one-article/"+b,success:function(a){console.log(a);var b="";if(a){$("#article-container").html("");b+="<h2>"+a.title+"</h2><br />";b+="<div>"+a.content+"</div>";b+="<hr />";b+="<p><small>"+a.date+"</small></p>";$("#article-container").append(b)}}})};return a}$(function(){$.ajax({type:"GET",url:"/article/list-articles",success:function(a){var b="";b+='<ul class="nav nav-pills nav-stacked">';for(var c=0,d=a.length;c<d;c++)b+='<li><a data-id="'+a[c].article_id+'">'+a[c].title+"</a></li>";b+="<ul>";$("#article-list").append(b);Article().checklist()}})});