var Life={start:function(){var b=this;$.ajax({type:"GET",url:"/article/fetch-articles",beforeSend:function(){Loader.show()},success:function(a){var b=(new EJS({url:"/global/template/article.ejs"})).render(a);$main.append(b)}})},firstLoad:function(b){var c=this,d=["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"],e,f,g,h,i;e=new Date;f=e.getFullYear();g=d[e.getMonth()];h=g+"-"+f;$.ajax({type:"GET",url:"/get-article/"+h,dataType:"json",success:function(a){if(a){Loader.remove();$main.append(b);$("#article-date").children("li").data("id")===h&&$("#article-date").children("li").addClass("active");if(a.length>0){i=c.renderArticle(a);$("#article").append(i)}else $("#article").append('<div class="pager"><h3>There is no article in this month, because I\'m so lazy</h3></div>')}},complete:function(){$("#article-date").children("li").bind("click",function(){g=$(this).data("id");c.getArticle(g)})}})},getArticle:function(b){var c=this,d=["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"],e,f,g,h,i,j=$("#article-date").children("li");e=new Date;if(undefined!==b&&null!==b){f=e.getFullYear();g=b+"-"+f;Loader.show()}else{f=e.getFullYear();b=d[e.getMonth()];g=b+"-"+f}for(var k=0;k<12;k++){i=$(j[k]).data("id");if(i===g.split("-")[0]){j.removeClass("active");$(j[k]).addClass("active")}}$.ajax({type:"GET",url:"/get-article/"+g,dataType:"json",beforeSend:function(){$("#article").html("")},success:function(a){if(a){Loader.remove();if(a.length>0){h=c.renderArticle(a);$("#article").append(h)}else $("#article").append('<div class="pager"><h3>There is no article in this month, because I\'m so lazy</h3></div>')}}})},renderArticle:function(b){var c="";for(var d=0,e=b.length;d<e;d++){c+='<div class="row-fluid">';c+="<h2>"+b[d].k1+"</h2>";c+="<p><small>";b[d].k3!=="0"?c+="Last edited on "+timeHistory(b[d].k3):c+="Created on "+timeHistory(b[d].k2);c+="</small></p>";c+=b[d].k5;c+="<hr />";c+="</div>"}return c}};