var Life={start:function(){var b=this,c;$.ajax({type:"GET",url:"/article",success:function(a){$main.html("");$main.append(a)},complete:function(){b.getArticle();$("#article-date").children("li").bind("click",function(){c=$(this).data("id");b.getArticle(c)})}})},getArticle:function(b){var c=this,d=["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"],e,f,g,h,i,j=$("#article-date").children("li");e=new Date;if(undefined!==b){f=e.getFullYear();g=b+"-"+f}else{f=e.getFullYear();b=d[e.getMonth()];g=b+"-"+f}for(var k=0;k<12;k++){i=$(j[k]).data("id");if(i===g.split("-")[0]){j.removeClass("active");$(j[k]).addClass("active")}}$.ajax({type:"GET",url:"/get-article/"+g,dataType:"json",beforeSend:function(){$("#article").html("");Loader.show()},success:function(a){if(a){$("#article").html("");Loader.remove();if(a.length>0){h=c.renderArticle(a);$("#article").append(h)}else $("#article").append('<div class="pager"><h3>There is no article in this month, because I\'m so lazy</h3></div>')}}})},renderArticle:function(b){var c="";for(var d=0,e=b.length;d<e;d++){c+='<div class="row-fluid">';c+="<h2>"+b[d].k1+"</h2>";c+="<p><small>";b[d].k3!=="0"?c+="Last edited on "+timeHistory(b[d].k3):c+="Created on "+timeHistory(b[d].k2);c+="</small></p>";c+=b[d].k5;c+="<hr />";c+="</div>"}return c}};